import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Users } from 'src/app/Model/users';
import { UserService } from 'src/app/Service/user.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  currentUser: Users | null;
  toggleBool: boolean=true;
  isInputVisible: boolean = false;
  updateForm: FormGroup;
  


  constructor(private fb: FormBuilder, private userService: UserService) {
    this.currentUser = this.userService.getCurrentUser();    
    this.updateForm = this.fb.group({
      userName: [this.currentUser?.userName, [ Validators.maxLength(20)]],
      userMail: [this.currentUser?.userMail,  [Validators.email]],
      password: [this.currentUser?.password, [Validators.minLength(5)]],      
      userId : [this.currentUser?.userId]
    });
  }

  deleteUser(){
    this.userService.deleteUser(this.currentUser?.userName).subscribe();
  }

  updateUser(){
    if (this.currentUser !== null){
    this.userService.updateUser(this.currentUser.userId, this.currentUser).subscribe();
    } else {
      console.log("L'id est null !");
      
    }
  }
  
  onSubmit(event: Event) {
    if (this.updateForm.valid) {
      const formValue: Users = this.updateForm.value;

      this.userService.updateUser(formValue.userId, formValue).subscribe(
        (response: Users) => {
          this.isInputVisible = false;
          alert('Vos informations on étaient mises à jour');
          console.log('Les information ont été mises à jour :', response);
        },
        (error: HttpErrorResponse) => {
          alert('Une erreur est survenue lors de la modification');
          console.error('Erreur lors de la modification :', error);
        }
      );
    } else {
      alert('Le formulaire de la modification est invalide');
      console.log('Le formulaire de la modification est invalide');
    }
  }
  
  visibilityInput(){
    this.isInputVisible = true;
  }
  
// Function pour déverrouiller le bouton supprimer profil
 changeEvent(event: any) {
        if (event.target.checked) {
            this.toggleBool= false;
        }
        else {
            this.toggleBool= true;
        }
    }
}
<div class="wrapper">
  <div *ngIf="currentUser">
    <h2>Bonjour, {{ currentUser.userName }}</h2>
    <h3>Voici vos informations personnelles:</h3>
    <div *ngIf="!isInputVisible">
      <p>Votre nom: {{ currentUser.userName }}</p>
      <p>Votre adresse mail: {{ currentUser.userMail }}</p>
      <p>Votre mot de passe: {{ currentUser.password }}</p>
      <button (click)="visibilityInput()">Modifier votre profil</button>
    </div>
    <input
      class="Visible"
      type="checkbox"
      name="delete"
      id="delete"
      (change)="changeEvent($event)"
    />
    <label for="delete"
      >Souhaitez-vous supprimer votre profil ? Cochez si oui</label
    >
    <br />
    <button (click)="deleteUser()" [disabled]="toggleBool">
      Supprimer mon profil
    </button>
    <br />
  </div>
  <br />

  <form
    *ngIf="isInputVisible"
    [formGroup]="updateForm"
    (submit)="onSubmit($event)"
  >
    <div>
      <label for="userName">Votre Nom :</label>
      <input type="text" id="userName" formControlName="userName" />
      <div *ngIf="updateForm.get('userName')?.hasError('maxlength')">
        <p>
          Votre nom doit faire moins de
          {{
            updateForm.get("userName")?.errors?.["maxlength"]?.requiredLength
          }}
          caractères.
        </p>
      </div>
    </div>
    <br />
    <div>
      <label for="userMail">Votre email :</label>
      <input type="email" id="userMail" formControlName="userMail" />
      <div *ngIf="updateForm.get('userMail')?.hasError('userMail')">
        <p>Email invalide.</p>
      </div>
    </div>
    <br />
    <div>
      <label for="password">Votre mot de passe :</label>
      <input type="password" id="password" formControlName="password" />
      <div *ngIf="updateForm.get('password')?.hasError('minlength')">
        <p>
          Mot de passe doit avoir au moins
          {{
            updateForm.get("password")?.errors?.["minlength"]?.requiredLength
          }}
          caractères.
        </p>
      </div>
    </div>
    <br />
    <button class="form-button" type="submit" [disabled]="updateForm.invalid">
      Appliquer les modifications
    </button>
    <br />
  </form>

  <div *ngIf="!currentUser">
    <p>Aucun utilisateur connecté.</p>
  </div>
</div>
