import { Component } from '@angular/core';
import { Users } from './Model/users';
import { UserService } from './Service/user.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Certif';
  currentUser: Users | null;

  constructor(public userService: UserService) {
    this.currentUser = this.userService.getCurrentUser();
  } 

  logout() {
    this.userService.logout(); // Appel à la méthode de déconnexion
    this.userService.setCurrentUser(null); // Réinitialisation de l'utilisateur actuel
    alert('Vous venez de vous déconnecter.')
    console.log('Utilisateur déconnecté');
  }
}
<div class="container">
  <header class="header">
    <a routerLink="/channel/channel3" class="title">
      <h1>TinySlack</h1>
    </a>
    <nav>
      <a *ngIf="userService.isLoggedInUser()" routerLink="/profile"
        ><img class="icon-profile" src="assets/images/icon-profile.png"
      /></a>
      <a *ngIf="!userService.isLoggedInUser()" routerLink="/login"
        ><img class="icon-profile" src="assets/images/icon-profile.png"
      /></a>
    </nav>
  </header>

  <div class="channel-list"><app-list-channel></app-list-channel></div>

  <div class="content"><router-outlet></router-outlet></div>

  <footer class="footer">
    <a routerLink="/about" class="about">A propos</a>
    <a routerLink="/channel/channel3" class="footer-a">
      <p class="footer-logo">TinySlack</p>
    </a>
    <button
      *ngIf="userService.isLoggedInUser()"
      routerLink="/login"
      class="form-button, deconnexion"
      type="submit"
      (click)="logout()"
    >
      Déconnexion
    </button>
  </footer>
</div>
