import { Component } from '@angular/core';
import { Users } from './Model/users';
import { UserService } from './Service/user.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Certif';
  currentUser: Users | null;

  constructor(public userService: UserService) {
    this.currentUser = this.userService.getCurrentUser();
  } 

  logout() {
    this.userService.logout(); // Appel à la méthode de déconnexion
    this.userService.setCurrentUser(null); // Réinitialisation de l'utilisateur actuel
    alert('Vous venez de vous déconnecter.')
    console.log('Utilisateur déconnecté');
  }
}
<header>
  <h1>TinySlack</h1>
  <nav>
    <a *ngIf="userService.isLoggedInUser()" routerLink="/profile"
      ><img class="icon-profile" src="assets/images/icon-profile.png"
    /></a>
    <a *ngIf="!userService.isLoggedInUser()" routerLink="/login"
      ><img class="icon-profile" src="assets/images/icon-profile.png"
    /></a>
  </nav>
</header>

<router-outlet></router-outlet>

<footer>
  <button
    *ngIf="userService.isLoggedInUser()"
    routerLink="/login"
    class="form-button"
    type="submit"
    (click)="logout()"
  >
    Déconnexion
  </button>
  <a routerLink="/about">A propos</a>
</footer>
